function heroOrGameplay(element) {
    if(element.value == "gameplay") {
        document.getElementById('hero-form').style.display = 'none';
    } else {
        document.getElementById('hero-form').style.display = 'block';
    }
}

function generateSlug(element) {
    console.log("keyup");
    var slugInput = document.getElementById('url-slug').value = convertToSlug(element.value);
}
function convertToSlug(Text) {
    return Text
        .toLowerCase()
        .replace(/[^\w ]+/g,'')
        .replace(/ +/g,'-');
}